const a=()=>{const e=document.querySelector(".header"),t=window.pageYOffset||document.documentElement.scrollTop;e.classList.toggle("scrolled",t>50)},n=()=>{const e=document.querySelectorAll(".nav-link"),t=window.location.pathname.split("/")[1]||"Inicio";e.forEach(o=>{o.classList.toggle("active",o.getAttribute("data-active").toLowerCase()===t.toLowerCase())})};document.addEventListener("astro:page-load",()=>{window.addEventListener("scroll",a),n()});document.addEventListener("astro:after-swap",n);
